const SEED_X = 1;
const SEED_Y = 0;

const arr: number[] = new Array(1024).fill(0);

async function start() {
  const wallet = new Wallet(
    'f421082ca89504c15205c3fe63761b479fa0f538b0c5c14f9402d68ae7f3d08b',
    new JsonRpcProvider(
      'https://polygon-mumbai.infura.io/v3/fbbc7b4004ae43458d9483ce3ff2c715'
    )
  );

  const addressesPath = `${process.cwd()}/addresses/${80001}.json`;
  const addressBook = JSON.parse(
    await fs.readFileSync(addressesPath).toString()
  );

  const contract = TerraMasu__factory.connect(addressBook.contract, wallet);

  const seed = await contract.createSeed(SEED_X, SEED_Y);
  await seed.wait(2);

  console.log('Created seed');

  const receipt = await contract.createTile(SEED_X, SEED_Y, newData);
  await receipt.wait(2);
  console.log('Created tile');
}

start().catch((e: Error) => {
  console.error(e);
  process.exit(1);
});
